import React from "react";
import { NavLink } from "react-router-dom";



class Products extends React.Component {  
    state = {
        products: []
      };
    // using a componentDidMount here to retrieve my products from my endpoint
    componentDidMount() {
      // fetching the products
      const url = "http://localhost:5001/api/Products";
      fetch(url)
      // want to turn into an json object
      .then((response) => {
        return response.json();
      })
      // change the products state and retrieving the data of products
        .then((data) => {
          this.setState({products:data})
        })
      .catch((error) => {
        console.log('BAD', error)
      });
    }  

    render() {  

        return (  
            <section>  
                <h1>Products List</h1>  
                <div>  
                    <table>  
                        <thead><tr><th>Product Id</th><th>Product Name</th><th>Product Category</th><th>Product Price</th></tr></thead>  
                        <tbody>  
                            {  
                                this.state.ProductData.map((p, index) => {  
                                    return <tr key={index}><td>{p.ProductId}</td><td> {p.ProductName}</td><td>{p.ProductCategory}</td><td>{p.ProductPrice}</td></tr>;  
                                })  
                            }  
                        </tbody>  
                    </table>  
                </div>  


            </section>  
        )  
    }  
}  


