import React, { useState, useEffect } from "react";
import axios from "axios";

const UpdateCard = ({ name, category, description }) => {
  const [id, setID] = useState([]);
  const [prodname, setProductName] = useState([]);
  const [proddescription, setProductDescription] = useState([]);
  const [prodcategory, setCategory] = useState([]);
  const [products, setProducts] = useState();

  const handleUpdate = () => {
    const url = `/api/products/${id}`;
    axios
      .put(url, {
        productId: parseInt(id),
        ProductName: name,
        ProductDescription: description,
        Category: category
      })
      .catch(err => console.log(err));
  };
  return (
    <div className="card-main">
      <div className="card-row">
        <div className="card-container">
          <div className="card-body">
            <h2 className="strong">{name}</h2>
            <p className="card-text">
              {category}
              <br />
              {description}
            </p>
            <h2>Update</h2>
            <label>Id:</label>
            <input onChange={e => setID(e.target.value)} />
            <label>Name:</label>
            <input onChange={e => setProductName(e.target.value)} />
            <label>Description:</label>
            <input onChange={e => setDescr(e.target.value)} />
            <label>Quantity:</label>
            <input onChange={e => setQuantity(e.target.value)} />
            <label>Type:</label>
            <input onChange={e => setType(e.target.value)} />
            <label>Image Url:</label>
            <input onChange={e => setUrl(e.target.value)} />
            <button onClick={handleUpdate}>Update</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UpdateCard;
