import React from "react";
import { NavLink } from "react-router-dom";

const Card = ({ name, category, description }) => {
  const [products, setProducts] = useState();
  const [id, setId] = useState();
  // using a componentDidMount here to retrieve my products from my endpoint

  handleDelete = () => {
    axios.delete(`/api/Products/${id}`);
  };
  return (
    <div className="card-main">
      <div className="card-row">
        <div className="card-container">
          <div className="card-body">
            <h2 className="strong">{name}</h2>
            <p className="card-text">
              {category}
              <br />
              {description}
            </p>
            <NavLink className="btn btn-secondary" to="/details">
              Edit
            </NavLink>
            <button className="btn btn-secondary ml-1">Delete</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Card;
