import React from "react";
import { NavLink } from "react-router-dom";
import axios from "axios"

class Products extends React.Component {
  state = {
    products: []
  };
  // using a componentDidMount here to retrieve my products from my endpoint
  componentDidMount() {
    // fetching the products
    const url = "/api/Products/";
    // try{
    fetch(url )
    // // ,{
    // //     headers : {
    // //         "Content-Type": "application/json",
    // //         "Accept": 'application/json'
    // //     }
    // // })
      // want to turn into an json object
      .then(response => {
      response.json();

      })
    //   // change the products state and retrieving the data of products
      .then(data => {
        this.setState({ products: data });
        console.log("hey", this.state.products);
      })
    }catch (error){
     console.log(error)
    }
//     axios.get('/api/Products')
//   .then(function (response) {
//     console.log(response);
//   })
//   .catch(function (error) {
//     // handle error
//     console.log(error);
//   })}
  render() {
    // const Products = () => {
    return (
      <section id="Products">
        <div className="container">
          <div className="row">
            <div className="col">
              <div className="card">
                <div className="card-header">
                  <h4>Latest Products</h4>
                </div>
                <table className="table table-striped">
                  <thead className="thead-dark">
                    <tr>
                      <th>#</th>
                      <th>Product Name</th>
                      <th>Category</th>
                      <th>Date</th>
                      <th />
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                        {this.state.products &&
                          this.state.products.map(product => (
                              <td>
                              key={product.ProductName}
                              name={product.ProductName}
                              price={product.price}
                              description={product.ProductDescription}
                              image={product.Image}
                              </td>
                          ))}
                      <td>
                        <NavLink className="btn btn-secondary" to="/details">
                          <i className="fas fa-angle-double-right" /> Details
                        </NavLink>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default Products;
