import React, { useState, useEffect } from "react";
import axios from "axios";
import ProductModal from "./ProductModal";
import Modal from "react-bootstrap4-modal";
const Card = ({ id, name, category, description }) => {
  const [products, setProducts] = useState();

  useEffect(() => {
    axios
      .get("/api/Products")
      .then(({ data }) => {
        console.log(data);
        setProducts(data);
      })
      .catch(function(error) {
        // handle error
        console.log(error);
      });
  }, []);

  const handleDelete = () => {
    axios.delete(`/api/products/${id}`);
  };

  return (
        <>
      <Modal visible={true} onClickBackdrop={this.modalBackdropClicked}>
        <div className="modal-header">
          <h5 className="modal-title">Red Alert!</h5>
        </div>
        <div className="modal-body">
          <p>Enemy vessel approaching!</p>
        </div>
        <div className="modal-footer">
          <button
            type="button"
            className="btn btn-secondary"
            onClick={this.onPanic}
          >
            Panic
          </button>
          <button
            type="button"
            className="btn btn-primary"
            onClick={this.onFirePhasers}
          >
            Fire phasers
          </button>
        </div>
      </Modal>
    <div className="card-main">
      <div className="card-row">
        <div className="card-container">
          <div className="card-body">
            <h2 className="strong">{name}</h2>
            <p className="card-text">
              {category}
              <br />
              {description}
            </p>
            <a
              href="#"
              className="btn btn-secondary"
              data-toggle="modal"
              data-target="#ProductModal"
            >
              Edit
            </a>
            <button onClick={handleDelete} className="btn btn-secondary ml-1">
              Delete
            </button>
          </div>
        </div>
      </div>
    </div>
    </>
  );
};

export default Card;
