import React, { useState } from "react";
import axios from "axios";

const UpdateCard = ({ name, category, description, id }) => {
  const [Id, setID] = useState([]);
  const [prodname, setProductName] = useState([]);
  const [proddescription, setProductDescription] = useState([]);
  const [prodcategory, setCategory] = useState([]);
  const [products, setProducts] = useState();

  const handleUpdate = () => {
    axios
      .put(`/api/Products/${Id}`, {
        Id: id,
        ProductName: name,
        ProductDescription: description,
        Category: category
      })
      .catch(err => console.log(err));
  };
  return (
    <div className="card-main">
      <div className="card-row">
        <div className="card-container">
          <div className="card-body">
            <h2 className="strong">{name}</h2>
            <p className="card-text">
              {category}
              <br />
              {description}
            </p>
            <label>Id:</label>
            <input onChange={e => setID(e.target.value)} />
            <label>Name:</label>
            <input onChange={e => setProductName(e.target.value)} />
            <label>Description:</label>
            <input onChange={e => setProductDescription(e.target.value)} />
            <label>Category:</label>
            <input onChange={e => setCategory(e.target.value)} />
            <button onClick={handleUpdate} className="btn btn-secondary ml-1">
              Update
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UpdateCard;
